var OC_APP_URL = 'https://app.helpninja.com';
function createIframe() {
  var talkformContainer = document.getElementById('container-talkform');
  if (!talkformContainer) {
    
    var formContainer = '<div id="optinchat-container" style="position: fixed;width: 0px;height: 0px;bottom: 0px;right: 0px;z-index: 2147483647;"><div class="optinchat-app optinchat-app-launcher-enabled"><span>';
    var iframeAppHTML = '<div class="optinchat-messenger-frame" id="oc_app_iframe" style="display:none" ><iframe allowfullscreen id="oc_messenger_frame" title=""></iframe></div></span>'
    var iframeHTML = '<iframe id="oc_iframe" class="optinchat-launcher-frame"></iframe>'
    var formEndingTags = '</div></div></div>';
    var d1 = document.getElementsByTagName("body")[0];;
    d1.insertAdjacentHTML('beforeend', formContainer + iframeAppHTML + iframeHTML + formEndingTags);
  }
}




function optinchat_injectCss(css) {
  /** @type {Element} */
  var style = document.createElement("style");
  /** @type {string} */
  style.type = "text/css";
  /** @type {string} */
  style.rel = "stylesheet";
  style.media = "all";
  try {
    /** @type {string} */
    style.styleSheet.cssText = css;
  } catch (t) {}
  try {
    /** @type {string} */
    style.innerHTML = css;
  } catch (t) {}
  document.getElementsByTagName("head")[0].appendChild(style);
}


function optinchat_getCss() {
  return '.optinchat-mobile-messenger-active {overflow: hidden;height: 100%;width: 100%;position: fixed;}';
}

optinchat_injectCss(optinchat_getCss());

var oc_isMobile = null;


window.addEventListener('message', function(e) {

  if (e.origin === OC_APP_URL) {
    var message = e.data;
    var isMobileClose = false;
    if (message.isMobile) {
      oc_isMobile = message.isMobile;
    }
    if (message.isMobileClose) {
      isMobileClose = message.isMobileClose;
    }
    if (message.needLocation) {
      var receiver = document.getElementById('oc_iframe').contentWindow;
      receiver.postMessage({
        parentURL: window.location.href
      }, '*');
      return;
    }

    if (message.hideOptinchat) {
      var appCont = document.getElementById('container-talkform');
      if (appCont) {
        appCont.remove();
      }
      return;
    }

    var container = document.getElementById('container-talkform');
    var appIframe = document.getElementById('oc_app_iframe');

    if (message.isShowed) {
      if (oc_isMobile) {
        // container.style.height = '100%';
        // container.style.width = '100%';
        appIframe.style.display = 'block';
        var body = document.getElementsByTagName('body');
        if (body && body.length > 0) {
          body = body[0];
          body.classList.add("optinchat-mobile-messenger-active");
        }
        return;
      }

      // container.style.opacity = 0.9;
      // setTimeout(()=>{
      //     container.style.opacity = 1;
      // },100);
      appIframe.style.display = 'block';


      appIframe.classList.add('optinchat-messenger-frame-enter');
      setTimeout(() => {
        appIframe.classList.add('optinchat-messenger-frame-enter-active');
        setTimeout(() => {
          appIframe.classList.remove('optinchat-messenger-frame-enter');
          appIframe.classList.remove('optinchat-messenger-frame-enter-active');
        }, 100);
      }, 100);
      return;
    } else {
      if (appIframe && oc_isMobile) {
        appIframe.style.display = 'none';
        var body = document.getElementsByTagName('body');
        if (body && body.length > 0) {
          body = body[0];
          body.classList.remove("optinchat-mobile-messenger-active");
        }
        if (isMobileClose) {
          var autoPilotIframe = document.getElementById('oc_iframe').contentWindow;
          autoPilotIframe.postMessage({
            toggleIntercom: true
          }, '*');
        }
      } else if (appIframe) {
        appIframe.classList.add('optinchat-messenger-frame-leave');
        setTimeout(() => {
          appIframe.classList.add('optinchat-messenger-frame-leave-active');
          setTimeout(() => {
            appIframe.classList.remove('optinchat-messenger-frame-leave');
            appIframe.classList.remove('optinchat-messenger-frame-leave-active');
            appIframe.style.display = 'none';
          }, 100)
        }, 100);
      }
    }
  }

});
var OC_DOM = new function() {
  var IS_READY = false;
  var CALLBACKS = [];
  var SELF = this;

  SELF.ready = function(callback) {
    //check to see if we're already finished
    if (IS_READY === true && typeof callback === 'function') {
      callback();
      return;
    }

    //else, add this callback to the queue
    CALLBACKS.push(callback);
  };
  var addEvent = function(event, obj, func) {
    if (window.addEventListener) {
      obj.addEventListener(event, func, false);
    } else if (document.attachEvent) {
      obj.attachEvent('on' + event, func);
    }
  };
  var doScrollCheck = function() {
    //check to see if the callbacks have been fired already
    if (IS_READY === true) {
      return;
    }

    //now try the scrolling check
    try {
      document.documentElement.doScroll('left');
    } catch (error) {
      setTimeout(doScrollCheck, 1);
      return;
    }

    //there were no errors with the scroll check and the callbacks have not yet fired, so fire them now
    fireCallbacks();
  };
  var fireCallbacks = function() {
    //check to make sure these fallbacks have not been fired already
    if (IS_READY === true) {
      return;
    }

    //loop through the callbacks and fire each one
    var callback = false;
    for (var i = 0, len = CALLBACKS.length; i < len; i++) {
      callback = CALLBACKS[i];
      if (typeof callback === 'function') {
        callback();
      }
    }

    //now set a flag to indicate that callbacks have already been fired
    IS_READY = true;
  };
  var listenForDocumentReady = function() {
    //check the document readystate
    if (document.readyState === 'complete') {
      return fireCallbacks();
    }

    //begin binding events based on the current browser
    if (document.addEventListener) {
      addEvent('DOMContentLoaded', document, fireCallbacks);
      addEvent('load', window, fireCallbacks);
    } else if (document.attachEvent) {
      addEvent('load', window, fireCallbacks);
      addEvent('readystatechange', document, fireCallbacks);

      //check for the scroll stuff
      if (document.documentElement.doScroll && window.frameset === null) {
        doScrollCheck();
      }
    }
  };

  //since we have the function declared, start listening
  listenForDocumentReady();
};


function ocAppendScript(src) {
  var n = document.createElement("script");
  return n.type = "text/javascript",
  n.charset = "utf-8",
  n.src = src,
  n
}


